Boolean isDayValid = false;int day;do{    Console.Write("Введите день недели: ");    day = Convert.ToInt32(Console.ReadLine());    if (day > 0 && day <= 7)    {        isDayValid = true;    }} while (!isDayValid);if (day == 6 || day == 7){    Console.WriteLine($"{day} -> да");}else{    Console.WriteLine($"{day} -> нет");}